<?phpif ($view_mode != 'teaser') {  $account = user_load($uid);  if ($account->data['contact'] == 1) {    // Contact form is active, so we can use it if...    if (!$content['field_contact_email'][0]['#markup'] || empty($content['field_contact_email'][0]['#markup'])) {      // user the contact form      $contact_button = l('Click here to email seller', 'user/' . $account->uid . '/contact');    }    else {      $contact_button = '<a href="mailto:' . $content['field_contact_email'][0]['#markup'] . '">Click here to email seller</a>';    }  }  else {    $contact_button = '<a href="mailto:' . $content['field_contact_email'][0]['#markup'] . '">Click here to email seller</a>';  }  $content['field_contact_email'][0]['#markup'] = $contact_button;}?><?phpswitch ($view_mode) {  case 'teaser':    ?>    <?php    // get the link to the car    $link = l('See More', 'node/' . $nid, array('attributes' => array('class' => array('btn', 'btn-primary'))));    ?>    <h3 class="car-title"><?php print $title; ?></h3>    <?php    hide($content['field_vehicle_image']);    ?>    <div class="vehicle--motherhood">    <?php    print render($content) . $link;    ?>    </div>    <?php    print render($content['field_vehicle_image']);    ?><?php    break;  default:    ?>åå    <div id="node-<?php print $node->nid; ?>"         class="<?php print $classes; ?> clearfix"<?php print $attributes; ?>>      <?php print render($title_prefix); ?>      <?php if (!$page): ?>        <h2<?php print $title_attributes; ?>>          <a href="<?php print $node_url; ?>"><?php print $title; ?></a>        </h2>      <?php endif; ?>      <?php print render($title_suffix); ?>      <div class="node-content clearfix"<?php print $content_attributes; ?>>        <?php        // We hide the comments and links now so that we can render them later.        hide($content['comments']);        hide($content['links']);        print render($content);        ?>      </div>      <?php if ($display_submitted): ?>        <div class="meta submitted clearfix">          <?php print $submitted; ?>        </div>      <?php endif; ?>      <?php      // Remove the "Add new comment" link on the teaser page or if the comment      // form is being displayed on the same page.      if ($teaser || !empty($content['comments']['comment_form'])) {        unset($content['links']['comment']['#links']['comment-add']);      }      // Only display the wrapper div if there are links.      $links = render($content['links']);      if ($links):        ?>        <div class="link-wrapper">          <?php print $links; ?>        </div>      <?php endif; ?>      <?php print render($content['comments']); ?>    </div>    <?php    break;}?>